    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        
        
        if(rbData.isSelected()){
            jobType = "Database Administrator";
        } else if (rbSoft.isSelected()){
            jobType = "Software Developer";
        } else if (rbGame.isSelected()){
            jobType = "Game Developer";
        } else if (rbWeb.isSelected()){
            jobType = "Web Designer";
        } else if (rbHard.isSelected()){
            jobType = "Hardware Engineer";
        } else if (rbNet.isSelected()){
            jobType = "Network Administrator";
        }
        
        try {
            int id = 146;
            String stts = "Pending";
            String sql;
            sql = "INSERT INTO registered VALUES ('"+ id++ + "','" + a +"','"+ b +"','"+ c +"','"+ d +"','"+ e +"','"+ f +"','" + jobType + "','" + stts + "')";
            java.sql.Connection conn = (Connection)confiig.configDB();
            java.sql.PreparedStatement pstm = conn.prepareStatement(sql);
            pstm.execute();
            JOptionPane.showMessageDialog(null, "Data has been successfully stored..");
            
        } catch (HeadlessException | SQLException e){
            JOptionPane.showMessageDialog(this, e.getMessage());
        }
        
        page3 form3 = new page3 ();
        form3.setVisible(true);
        this.setVisible(false);
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

